                        )}
                    </Formik>
                </div>
            </div>
        </div>
      </div>
    </div>
  );
};

const LoginPage = () => {
  const [authToken, setAuthToken] = useState('');

  useEffect(() => {
    const storedToken = localStorage.getItem('authToken');

    if (storedToken) {
      setAuthToken(storedToken);
    }
  }, []);

  let dashboardComponent;
  if (authToken) {
    const decodedToken = jwt_decode(authToken);
    const userRole = decodedToken ? decodedToken.role : '';

    if (userRole === 'admin') {
      dashboardComponent = <AdminDashboard />;
    } else if (userRole === 'general') {
      dashboardComponent = <GeneralUserDashboard />;
    }
  }

  const handleLogout = () => {
    setAuthToken('');
    localStorage.removeItem('authToken');
  };

  return (
    <div>
      {!authToken ? (
        <LoginForm setAuthToken={setAuthToken} />
      ) : (
        <>
          {dashboardComponent}
          <button
            className="absolute top-[135px] md:top-[15%] 2xl:top-[16%] right-[10%] xl:right-[15%] p-3 bg-red-500 hover:bg-red-600 transition ease-in-out duration-500 text-white rounded-lg font-semibold"
            onClick={handleLogout}
          >
            Logout
          </button>
        </>
      )}
    </div>
  );
};

export default LoginPage;
