import React, { useState } from 'react';
import AnimatedNumbers from 'react-animated-numbers';
import { useEffect } from 'react';
import { CSSTransition } from 'react-transition-group';
import { HorizontalTicker } from 'react-infinite-ticker';

export default function Home() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const slides = [
    { image: '/download.jpg', caption: 'Slide 1' },
    { image: '/download-1.jpg', caption: 'Slide 2' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((currentIndex) =>
        currentIndex + 1 > slides.length - 1 ? 0 : currentIndex + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="overflow-clip">
      <div
        className="h-screen w-screen bg-cover"
        style={{ backgroundImage: `url(${process.env.PUBLIC_URL + '/homebgfinal.png'})` }}
      >
        <div className="pt-[30%] md:pt-[12%] 2xl:pt-[12%] font-semibold pl-12 md:pl-32 h-screen pr-16 md:w-1/2 md:pr-0 xl:pl-52">
          <div className="text-6xl xl:text-7xl 2xl:text-[115px] text-white font-extrabold">
            Infrastructure Built to Last
          </div>
          <div className="text-2xl text-blue-200 font-semibold">
            Building strong foundations for over{' '}
            <span className="inline-block">
              <AnimatedNumbers
                includeComma
                animateToNumber={60}
                configs={[
                  { mass: 1, tension: 220, friction: 100 },
                  { mass: 1, tension: 180, friction: 130 },
                  { mass: 1, tension: 280, friction: 90 },
                  { mass: 1, tension: 180, friction: 135 },
                  { mass: 1, tension: 260, friction: 100 },
                  { mass: 1, tension: 210, friction: 180 }
                ]}
              />
            </span>
            &nbsp;years
          </div>
          <a href="/employment">
            <button className="py-3 px-4 mt-3 bg-blue-600 shadow-lg hover:bg-blue-800 text-white w-auto transition ease-in duration-400 text-center text-xl font-bold rounded-lg">
              Join Us
            </button>
          </a>
        </div>
      </div>
      <div className="relative h-[200px] bg-white w-screen overflow-visible">
        <div className="absolute left-[-10px] top-[-45px] flex bg-white h-[50vh] w-[105%] md:w-[105%] justify-center pt-10 transform rotate-1">
          <div className="absolute left-[-15px] top-[30px] grid bg-blue-800 h-full w-[105%] md:w-[105%] content-center pt-10 transform rotate-1">
            <div className="h-[50%] flex justify-center text-5xl font-semibold -rotate-2 text-white">
              Projects
            </div>
            <div className="h-[50%] -rotate-2">
              <HorizontalTicker duration={5000}>
                {slides.map((slide, index) => (
                  <img key={index} src={slide.image} alt={slide.caption} />
                ))}
              </HorizontalTicker>
            </div>
          </div>
        </div>
      </div>
      <div className="h-[500px] bg-[#e9ecf8]"></div>
    </div>
  );
}
