import React from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import axios from 'axios';

const Employment = () => {
  const { handleSubmit, register, formState: { errors, isSubmitting } } = useForm({
    resolver: yupResolver(validationSchema),
  });

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Name is required'),
    phone: Yup.string().required('Phone number is required'),
    address: Yup.string().required('Address is required'),
    email: Yup.string().email('Invalid email address'),
    workExperience: Yup.string(),
    resume: Yup.mixed().required('Resume file is required'),
  });

  const onSubmit = async (data) => {
    try {
      const formData = new FormData();
      formData.append('name', data.name);
      formData.append('phone', data.phone);
      formData.append('address', data.address);
      formData.append('email', data.email);
      formData.append('workExperience', data.workExperience);
      formData.append('resume', data.resume[0]);

      await axios.post('http://localhost:3001/users/send-email', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Email sent successfully!');
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <>
      <div className="bg-cover bg-[#ffffff] bg-fixed pb-[10%]">
        <div className="px-[10%] mx-[8.5%] pt-[22%] md:pt-[10%] xl:pt-[8%] text-xl">
          <div className="text-4xl font-bold my-7">Employment</div>
          <div className="xl:bg-[#eef0f7] h-min rounded-xl xl:shadow-md xl:p-10">
            <div className="pb-4">
              Click&nbsp;
              <a
                className="font-bold text-blue-600 hover:text-blue-700 transition ease-in duration-200"
                href="/vectorapp.pdf"
                target="_blank"
                rel="noopener noreferrer"
              >
                HERE
              </a>
              &nbsp;to download the Vector Employment PDF. You can print, complete, and mail or fax to us.
            </div>
            <div className="text-2xl font-semibold mb-3">Job Description:</div>
            <p>
              We are seeking experienced and responsible construction personnel to join our heavy highway construction
              company. We want to offer you competitive pay, a generous benefits package, and opportunities for advancement.
              Join our team and learn how fulfilling it is to be rewarded appropriately for your efforts while you develop
              your skills and learn the newest construction techniques in a fast-paced environment.
            </p>
            <div className="text-2xl font-semibold my-3">Responsibilities:</div>
            <div className="flex flex-col space-y-0">
              <p>➮&nbsp;Report each day on time and ready to work.</p>
              <p>➮&nbsp;Have reliable transportation to the work site.</p>
              <p>➮&nbsp;Be able to perform construction tasks that involve strenuous manual labor.</p>
              <p>➮&nbsp;Ability to take and follow directions from superiors.</p>
              <p>➮&nbsp;Maintain a clean job site: pick up all tools and equipment and secure job site each day in order to
                eliminate potential hazards.</p>
              <p>➮&nbsp;Perform necessary tasks in a safe manner, in accordance with Vector Construction's Safety
                Policies.</p>
            </div>
            <div className="text-2xl font-semibold my-3">Qualifications:</div>
            <div className="flex flex-col space-y-0">
              <p>➮&nbsp;Valid driver's license is required.</p>
              <p>➮&nbsp;Able to pass a pre-employment physical, drug screen, and background check.</p>
              <p>➮&nbsp;Must have reliable transportation to the work site.</p>
              <p>➮&nbsp;Must be able to understand and follow specific directions.</p>
              <p>➮&nbsp;Experience in heavy construction work preferred.</p>
            </div>
            <div className="text-2xl font-semibold my-3">Benefits:</div>
            <div className="flex flex-col space-y-0">
              <p>➮&nbsp;Competitive wages and opportunity for advancement.</p>
              <p>➮&nbsp;Health and Dental Insurance.</p>
              <p>➮&nbsp;401K retirement plan.</p>
              <p>➮&nbsp;Paid vacation.</p>
              <p>➮&nbsp;Uniforms and boot allowance provided.</p>
            </div>
            <div className="text-2xl font-semibold my-3">How To Apply:</div>
            <p>
              If you feel you are qualified for this position, please complete the form below and upload your resume.
              Applications will be accepted by mail or fax as well. Thank you for considering Vector Construction Company
              for employment.
            </p>
            <div className="mt-7">
              <form onSubmit={handleSubmit(onSubmit)} className="rounded-lg py-9" encType="multipart/form-data">
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="name">
                    Name*
                  </label>
                  <input
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.name ? 'border-red-500' : ''}`}
                    {...register('name')}
                    placeholder="Name*"
                  />
                  {errors.name && <div className="text-red-500">{errors.name.message}</div>}
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="phone">
                    Phone*
                  </label>
                  <input
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.phone ? 'border-red-500' : ''}`}
                    {...register('phone')}
                    placeholder="Phone*"
                  />
                  {errors.phone && <div className="text-red-500">{errors.phone.message}</div>}
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="address">
                    Address*
                  </label>
                  <input
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.address ? 'border-red-500' : ''}`}
                    {...register('address')}
                    placeholder="Address*"
                  />
                  {errors.address && <div className="text-red-500">{errors.address.message}</div>}
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="email">
                    Email
                  </label>
                  <input
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.email ? 'border-red-500' : ''}`}
                    {...register('email')}
                    placeholder="Email"
                  />
                  {errors.email && <div className="text-red-500">{errors.email.message}</div>}
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="workExperience">
                    Work Experience
                  </label>
                  <textarea
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.workExperience ? 'border-red-500' : ''}`}
                    {...register('workExperience')}
                    placeholder="Work Experience"
                  ></textarea>
                  {errors.workExperience && <div className="text-red-500">{errors.workExperience.message}</div>}
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 font-medium mb-2" htmlFor="resume">
                    Resume*
                  </label>
                  <input
                    type="file"
                    accept=".pdf,.doc,.docx"
                    {...register('resume')}
                    className={`appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight bg-white ${errors.resume ? 'border-red-500' : ''}`}
                  />
                  {errors.resume && <div className="text-red-500">{errors.resume.message}</div>}
                </div>
                <div className="flex items-center justify-center">
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  >
                    {isSubmitting ? 'Submitting...' : 'Submit'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Employment;
