import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

function LoginForm({ setAuthToken }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send login request to the server
      const response = await axios.post('http://localhost:3001/users/login', { email, password });

      // Extract the token from the response
      const { token } = response.data;

      // Set the token as the authentication token
      setAuthToken(token);

      // Save the token to local storage
      localStorage.setItem('authToken', token);
    } catch (error) {
      setError('Invalid email or password');
      console.log(error);
    }
  };

  return (
    <div className="flex justify-center">
      <div className="h-screen grid content-center">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        <form onSubmit={handleSubmit} className="space-y-2">
          <div>
            <label className="block">Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="border p-2 rounded"
            />
          </div>
          <div>
            <label className="block">Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="border p-2 rounded"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Login
          </button>
          {error && <p>{error}</p>}
        </form>
      </div>
    </div>
  );
}

function AdminDashboard() {
  return <div>Admin Dashboard</div>;
}

function GeneralUserDashboard() {
  return <div className="h-screen">General User Dashboard</div>;
}

function LoginPage() {
  const [authToken, setAuthToken] = useState('');

  useEffect(() => {
    // Check if the token exists in local storage
    const storedToken = localStorage.getItem('authToken');

    if (storedToken) {
      setAuthToken(storedToken);
    }
  }, []);

  let dashboardComponent;
  if (authToken) {
    const decodedToken = jwt_decode(authToken);
    const userRole = decodedToken ? decodedToken.role : '';

    if (userRole === 'admin') {
      dashboardComponent = <AdminDashboard />;
    } else if (userRole === 'general') {
      dashboardComponent = <GeneralUserDashboard />;
    }
  }

  const handleLogout = () => {
    // Clear the authentication token and remove it from local storage
    setAuthToken('');
    localStorage.removeItem('authToken');
  };

  return (
    <div>
      {!authToken ? (
        <LoginForm setAuthToken={setAuthToken} />
      ) : (
        <>
          {dashboardComponent}
        </>
      )}
    </div>
  );
}

export default LoginPage;
