import React, { useState } from 'react';
import AnimatedNumbers from "react-animated-numbers";
import { useEffect } from 'react';
import "./Home.css";
import { HorizontalTicker } from "react-infinite-ticker";

export default function Home() {
    const [currentIndex, setCurrentIndex] = useState(0);

    const proj = [
        {
          id: 0,
          heading1: "Spring Hill",
          heading2: "Direct Applications Coordinator",
          backgroundColor: "hsl(119, 100%, 90.2%)",
        },
        {
          id: 1,
          heading1: "Euless",
          heading2: "National Solutions Representative",
          backgroundColor: "hsl(210, 100%, 90.2%)",
        },
        {
          id: 2,
          heading1: "Buena Park",
          heading2: "Chief Operations Liaison",
          backgroundColor: "hsl(273, 100%, 90.2%)",
        },
        {
          id: 3,
          heading1: "Euclid",
          heading2: "Customer Mobility Facilitator",
          backgroundColor: "hsl(168, 100%, 90.2%)",
        },
        {
          id: 4,
          heading1: "Madison",
          heading2: "Dynamic Applications Manager",
          backgroundColor: "hsl(161, 100%, 90.2%)",
        },
        {
          id: 5,
          heading1: "Sammamish",
          heading2: "National Identity Manager",
          backgroundColor: "hsl(172, 100%, 90.2%)",
        },
    ];      

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex(currentIndex + 1 > slides.length - 1 ? 0 : currentIndex + 1);
        }, 5000);
        return () => clearInterval(interval);
    }, [currentIndex, slides]);    

    return (
        <div className='overflow-clip'><div className="h-screen w-screen bg-cover" style={{ backgroundImage: `url(${process.env.PUBLIC_URL + '/homebgfinal.png'})` }}>
                <div className="pt-[30%] md:pt-[12%] 2xl:pt-[12%] font-semibold pl-12 md:pl-32 h-screen pr-16 md:w-1/2 md:pr-0 xl:pl-52">
                    <div className="text-6xl xl:text-7xl 2xl:text-[115px] text-white font-extrabold">
                        Infrastructure Built to Last
                    </div>
                    <div className="text-2xl text-blue-200 font-semibold">Building strong foundations for over <span className="inline-block"><AnimatedNumbers includeComma animateToNumber={60} configs={[{ mass: 1, tension: 220, friction: 100 }, { mass: 1, tension: 180, friction: 130 }, { mass: 1, tension: 280, friction: 90 }, { mass: 1, tension: 180, friction: 135 }, { mass: 1, tension: 260, friction: 100 }, { mass: 1, tension: 210, friction: 180 }]}/></span>
                    &nbsp;years</div>
                    <a href="/employment"> 
                        <button type="button" class="py-3 px-4 mt-3 bg-blue-600 shadow-lg hover:bg-blue-800 text-white w-auto transition ease-in duration-400 text-center text-xl font-bold rounded-lg">
                            Join Us
                        </button>
                    </a>
                </div>
            </div>
            <div className="relative h-[200px] bg-white w-screen overflow-visible">
                <div className="absolute left-[-10px] top-[-45px] flex bg-white h-[525px] w-[105%] md:w-[105%] justify-center pt-10 transform rotate-1">
                    <div className="absolute left-[-15px] top-[30px] grid bg-blue-800 h-full w-[105%] md:w-[105%] content-center pt-10 transform rotate-1 overflow-visible">
                        <div className="w-screen flex justify-center absolute top-[10%] text-5xl font-semibold -rotate-2 text-white">Projects</div>
                        <div className="w-[105%] -rotate-2 -mt-10">
                            <HorizontalTicker
                            duration={duration}
                            >
                                {proj.map(({ id, backgroundColor, heading1, heading2 }) => (
                                    <div className="box-wrapper box-wrapper--vertical" key={id}>
                                    <div className="box box--vertical" style={{ backgroundColor }}>
                                        <p className="heading-1">{heading1}</p>
                                        <p className="heading-2">{heading2}</p>
                                    </div>
                                    </div>
                                ))}
                            </HorizontalTicker>
                        </div>
                    </div>
                </div>
            </div>
            <div className='h-[500px] bg-[#e9ecf8]'></div>
        </div>
    );
}
