import React, { useState, useEffect } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import * as Yup from 'yup';

const LoginForm = ({ setAuthToken }) => {
  const handleLogin = async (values, { setSubmitting, setErrors }) => {
    try {
      const response = await axios.post('http://localhost:3001/users/login', values);
      const { token } = response.data;

      setAuthToken(token);
      localStorage.setItem('authToken', token);
    } catch (error) {
      setErrors({ password: 'Invalid email or password' });
      console.log(error);
    } finally {
      setSubmitting(false);
    }
  };

  const validationSchema = Yup.object().shape({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string().required('Password is required'),
  });

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-80 bg-[#f1f2f8] rounded-lg xl:p-10">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        <Formik
          initialValues={{ email: '', password: '' }}
          validationSchema={validationSchema}
          onSubmit={handleLogin}
        >
          {({ isSubmitting, errors, touched }) => (
            <Form className="space-y-2">
              <div>
                <label htmlFor="email" className="block">
                  Email:
                </label>
                <Field
                  id="email"
                  type="email"
                  name="email"
                  placeholder="Email*"
                  className="w-full px-4 py-2 rounded appearance-none border-2 border-gray-200 bg-white"
                />
                <ErrorMessage name="email" component="p" className="text-red-500" />
              </div>
              <div>
                <label htmlFor="password" className="block">
                  Password:
                </label>
                <Field
                  id="password"
                  type="password"
                  name="password"
                  placeholder="Password*"
                  className="w-full px-4 py-2 rounded appearance-none border-2 border-gray-200 bg-white"
                />
                <ErrorMessage name="password" component="p" className="text-red-500" />
              </div>
              <button
                type="submit"
                className="w-full bg-[#234d91] text-white px-4 py-2 rounded transition ease-in-out duration-500 font-semibold"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Logging in...' : 'Login'}
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
};

const AdminDashboard = () => {
  return <div>Admin Dashboard</div>;
};

const GeneralUserDashboard = () => {
  return (
    <div className="h-screen bg-cover bg-[#ffffff] bg-fixed pb-[10%]">
      <div className="px-[10%] mx-[8.5%] pt-[22%] md:pt-[8%]">
        <div className="text-4xl font-bold my-7">Dashboard</div>
        <div className="xl:bg-[#eef0f7] h-min rounded-xl xl:shadow-md xl:p-10">
          <div className="pb-4">Click&nbsp;<a className="font-bold text-blue-600 hover:text-blue-700 transition ease-in duration-200" href="/vectorapp.pdf" target="_blank" rel="noopener noreferrer">HERE</a>&nbsp;to download the Vector Employment PDF. You can print, complete, and mail or fax to us.</div>
        </div>
      </div>
    </div>
  );
};

const LoginPage = () => {
  const [authToken, setAuthToken] = useState('');

  useEffect(() => {
    const storedToken = localStorage.getItem('authToken');

    if (storedToken) {
      setAuthToken(storedToken);
    }
  }, []);

  let dashboardComponent;
  if (authToken) {
    const decodedToken = jwt_decode(authToken);
    const userRole = decodedToken ? decodedToken.role : '';

    if (userRole === 'admin') {
      dashboardComponent = <AdminDashboard />;
    } else if (userRole === 'general') {
      dashboardComponent = <GeneralUserDashboard />;
    }
  }

  const handleLogout = () => {
    setAuthToken('');
    localStorage.removeItem('authToken');
  };

  return (
    <div>
      {!authToken ? (
        <LoginForm setAuthToken={setAuthToken} />
      ) : (
        <>
          {dashboardComponent}
          <button
            className="absolute top-[135px] md:top-[15%] 2xl:top-[15%] right-[10%] xl:right-[18%] p-3 bg-red-500 hover:bg-red-600 transition ease-in-out duration-500 text-white rounded-lg font-semibold"
            onClick={handleLogout}
          >
            Logout
          </button>
        </>
      )}
    </div>
  );
};

export default LoginPage;
