import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import bcrypt from 'bcrypt';

function LoginForm({ setAuthToken }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/users/login', { email, password });
      const { token } = response.data;

      // Hash the token value before storing it
      const hashedToken = await bcrypt.hash(token, 10);

      setAuthToken(hashedToken);
      localStorage.setItem('authToken', hashedToken);
    } catch (error) {
      setError('Invalid email or password');
      console.log(error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-80 bg-gray-100 rounded-lg p-6">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        <form onSubmit={handleSubmit} className="space-y-2">
          <div>
            <label htmlFor="email" className="block">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <label htmlFor="password" className="block">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Login
          </button>
          {error && <p className="text-red-500">{error}</p>}
        </form>
      </div>
    </div>
  );
}

function AdminDashboard() {
  return <div>Admin Dashboard</div>;
}

function GeneralUserDashboard() {
  return (
    <div className="flex justify-center items-center h-screen">
      <div className="bg-gray-100 rounded-lg p-6">
        <div className="text-5xl font-bold">General User Dashboard</div>
      </div>
    </div>
  );
}

function LoginPage() {
  const [authToken, setAuthToken] = useState('');

  useEffect(() => {
    const storedToken = localStorage.getItem('authToken');

    if (storedToken) {
      // Compare the hashed token with the actual token using bcrypt.compare
      bcrypt.compare(storedToken, authToken, (err, result) => {
        if (result) {
          setAuthToken(storedToken);
        } else {
          // If the comparison fails, clear the stored token
          localStorage.removeItem('authToken');
        }
      });
    }
  }, []);

  let dashboardComponent;
  if (authToken) {
    const decodedToken = jwt_decode(authToken);
    const userRole = decodedToken ? decodedToken.role : '';

    if (userRole === 'admin') {
      dashboardComponent = <AdminDashboard />;
    } else if (userRole === 'general') {
      dashboardComponent = <GeneralUserDashboard />;
    }
  }

  const handleLogout = () => {
    setAuthToken('');
    localStorage.removeItem('authToken');
  };

  return (
    <div>
      {!authToken ? (
        <LoginForm setAuthToken={setAuthToken} />
      ) : (
        <>
          {dashboardComponent}
          <button className="absolute top-[15%] right-[10%] p-4 bg-red-300 rounded-lg font-semibold" onClick={handleLogout}>Logout</button>
        </>
      )}
    </div>
  );
}

export default LoginPage;
